.repository-details {
  padding: 20px;
}

.repository-header {
  text-align: center;
  margin-bottom: 20px;
}

.avatar {
  width: 100px;
  height: 100px;
  border-radius: 50%;
}

.repository-kpis {
  display: flex;
  justify-content: space-around;
  margin-bottom: 20px;
}

.kpi {
  text-align: center;
}

.issue-list {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
}

.issue-card {
  display: flex; /* Para criar um layout flexível */
  align-items: center; /* Centraliza verticalmente os itens */
  border: 1px solid #ddd;
  padding: 10px;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s;
  width: 100%; /* Ocupa 100% da largura do contêiner */
  max-width: 600px; /* Limita a largura máxima */
  margin-bottom: 10px;
  margin-top: 10px;
  height: auto; /* Remove a altura fixa */
  box-sizing: border-box;
}

.issue-card:hover {
  background-color: #f5f5f5;
}

.issue-info {
  display: flex;
  flex-direction: column; /* Alinha o título e a descrição em coluna */
}

h3, p {
  margin: 0;
  overflow: hidden;
  text-overflow: ellipsis; /* Adiciona "..." quando o texto for muito longo */
  white-space: nowrap; /* Impede que o texto quebre em várias linhas */
}

h3 {
  font-size: 1.2em;
  margin-bottom: 5px;
}

p {
  font-size: 0.9em;
  color: #666;
}

  
  <script>
  import axios from 'axios';
  
  export default {
    name: 'RepositoryDetails',
    props: {
      repo: Object
    },
    data() {
      return {
        issues: []
      };
    },
    async created() {
      await this.fetchIssues();
    },
    methods: {
      async fetchIssues() {
        try {
          const response = await axios.get(`https://api.github.com/repos/${this.repo.name}/issues`);
          this.issues = response.data;
        } catch (error) {
          console.error('Erro ao buscar issues:', error);
        }
      }
    }
  }
  </script>
  
  <style scoped>
  .repository-details {
    padding: 20px;
  }
  
  .repository-header {
    text-align: center;
    margin-bottom: 20px;
  }
  
  .avatar {
    width: 100px;
    height: 100px;
    border-radius: 50%;
  }
  
  .repository-kpis {
    display: flex;
    justify-content: space-around;
    margin-bottom: 20px;
  }
  
  .kpi {
    text-align: center;
  }
  
  .issue-list {
    display: flex;
    flex-wrap: wrap;
    gap: 20px;
  }
  
  .issue-card {
    border: 1px solid #ddd;
    padding: 20px;
    border-radius: 5px;
    width: 100%;
    max-width: 300px;
  }
  
  .issue-link {
    text-decoration: none;
    color: inherit;
  }
  
  .issue-card:hover {
    background-color: #f9f9f9;
  }
  </style>
