import Vue from 'vue';
import VueRouter from 'vue-router';
import SearchComponent from '../components/SearchComponent.vue';
import RepositoryList from '../components/RepositoryList.vue';
import RepositoryDetails from '../components/RepositoryDetails.vue';

Vue.use(VueRouter);

const routes = [
  { path: '/', component: SearchComponent },
  { path: '/list', component: RepositoryList },
  { path: '/details/:repoName', component: RepositoryDetails, props: true },
  { path: '*', redirect: '/' }, // Redireciona qualquer rota desconhecida para a página inicial
];

const router = new VueRouter({
  mode: 'history',
  routes,
});

export default router;
